import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;

import '../models/course.dart';
import '../variables/server_connection.dart';

enum AuthRole { admin, user, guest }
class Courses with ChangeNotifier {
   List<Course> _courses = [/*
    Course(
      id: 0,
      name: 'Math',
      imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQOE7SDFxv3AgoFuGqkQ1H8K30RTt_7Zcf7og&usqp=CAU',
    ),
    Course(
      id: 1,
      name: 'English',
      imageUrl:
      //'https://upload.wikimedia.org/wikipedia/commons/9/9a/English_language.png'
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkHqrf24sUqucwBmS7gneaFS7yN4rcUU5wgA&usqp=CAU',
    ),
    Course(
      id: 2,
      name: 'History',
      imageUrl: 'data:image/jpeg;base64,',
    )*/
  ];

   final String authToken;
   final String userEmail;
   final int userRole;// 0 admin //1 user

   Courses(this.authToken, this.userEmail, this.userRole, this._courses);

  List<Course> get courses {
    return [..._courses];
  }

  bool isEmpty() {
    return _courses.isEmpty;
  }


  Future<void> fetchAndSetCourses() async {
    final url = Uri.parse(
            '${ServerConnection.url}/courses'
                +';jsessionid=$authToken');
    try {
      final response = await http.get(url);
      //final extractedData = json.decode(response.body) as Map<String, dynamic>;
      final extractedData = json.decode(response.body) as List<dynamic>;
      if (extractedData == null) return;
      final List<Course> loadedCourses = [];
      //print(extractedData);
      for (var courseData in extractedData) {
        loadedCourses.add(Course(
          id: courseData['id'],
          name: courseData['nome'],
          imageUrl: courseData['imageUrl'],
        ));
      }
      loadedCourses.sort((a, b) => a.name.compareTo(b.name));
      _courses = loadedCourses;
      notifyListeners();

    } catch (error) {
    print(error);
    rethrow;
    }
  }


}